const { GoogleGenerativeAI } = require(\"@google/generative-ai\");
const fs = require(\"fs\");
const path = require(\"path\");

exports.run = {
  usage: ['hitamkan'], // Command utama
  hidden: [], // Command simpel (jika ada)
  use: 'Mengubah warna kulit dalam gambar menjadi hitam', // Deskripsi
  category: 'ai',
  async: async (m, { func, kuromi, isPrem }) => {
    const { quoted } = m;
    const q = quoted ? quoted : m;
    const mime = (q.msg || q).mimetype || \"\";

    if (!mime) return kuromi.reply(m.chat, `Kirim/reply gambar dengan caption *${func.prefix}hitamkan*`, m);
    if (!/image\/(jpe?g|png)/.test(mime)) return kuromi.reply(m.chat, `Format ${mime} tidak didukung! Hanya jpeg/jpg/png`, m);

    let promptText = \"Ubahlah Karakter Dari Gambar Tersebut Diubah Kulitnya Menjadi Hitam\";

    kuromi.reply(m.chat, \"Otw Menghitam...\", m);

    try {
      let imgData = await q.download();
      let genAI = new GoogleGenerativeAI(\"AIzaSyDdfNNmvphdPdHSbIvpO5UkHdzBwx7NVm0\");

      const base64Image = imgData.toString(\"base64\");

      const contents = [
        { text: promptText },
        {
          inlineData: {
            mimeType: mime,
            data: base64Image
          }
        }
      ];

      const model = genAI.getGenerativeModel({
        model: \"gemini-2.0-flash-exp-image-generation\",
        generationConfig: {
          responseModalities: [\"Text\", \"Image\"]
        },
      });

      const response = await model.generateContent(contents);

      let resultImage;
      let resultText = \"\";

      for (const part of response.response.candidates[0].content.parts) {
        if (part.text) {
          resultText += part.text;
        } else if (part.inlineData) {
          const imageData = part.inlineData.data;
          resultImage = Buffer.from(imageData, \"base64\");
        }
      }

      if (resultImage) {
        const tempPath = path.join(process.cwd(), \"tmp\", `gemini_${Date.now()}.png`);
        fs.writeFileSync(tempPath, resultImage);

        await kuromi.sendMessage(m.chat, {
          image: { url: tempPath },
          caption: `*Wahaha Makan Nih Hitam*`
        }, { quoted: m });

        setTimeout(() => {
          try {
            fs.unlinkSync(tempPath);
          } catch {}
        }, 30000);
      } else {
        kuromi.reply(m.chat, \"Gagal Menghitamkan.\", m);
      }
    } catch (error) {
      console.error(error);
      kuromi.reply(m.chat, `Error: ${error.message}`, m);
    }
  }
};
