const axios = require(\"axios\");
const cheerio = require(\"cheerio\");

async function scrapeKaoriSearch(query) {
    try {
        const response = await axios.get(`https://www.kaorinusantara.or.id/?s=${query}`);
        const html = response.data;
        const $ = cheerio.load(html);
        let results = [];

        $(\".td_module_10\").each((index, element) => {
            const title = $(element).find(\".entry-title a\").text().trim();
            const url = $(element).find(\".entry-title a\").attr(\"href\");
            const img = $(element).find(\".td-module-thumb img\").attr(\"src\");
            const date = $(element).find(\".td-post-date time\").text().trim();
            const author = $(element).find(\".td-post-author-name a\").text().trim();
            const category = $(element).find(\".td-post-category\").text().trim();
            const excerpt = $(element).find(\".td-excerpt\").text().trim();

            results.push({
                title,
                url,
                img,
                date,
                author,
                category,
                excerpt
            });
        });

        return results.length > 0 ? results : null;
    } catch (e) {
        return null;
    }
}

