async function scrapeKaoriSearch(query) {
    try {
        const response = await axios.get(`https://www.kaorinusantara.or.id/?s=${query}`);
        const html = response.data;
        const $ = cheerio.load(html);
        let results = [];

        $(\".td_module_10\").each((index, element) => {
            const title = $(element).find(\".entry-title a\").text().trim();
