// CREATE BY REZA DEVS KUROMI
// FITUR: REMOVE BACKGROUND (ILOVEIMG â†’ UGUU)

const axios = require(\"axios\");
const fs = require(\"fs\");
const FormData = require(\"form-data\");

// ====================== CORE REMOVE BG ======================
async function removebg(imagePath) {
  try {
    const html = await axios.get(\"https://www.iloveimg.com/remove-background\");

    const token = html.data.match(/\"token\":\"([^\"]+)\"/)?.[1];
    const task = html.data.match(/taskId\s*=\s*'([^']+)'/)?.[1];

    if (!token || !task) throw new Error(\"Gagal mendapatkan token / task ID\");

    const file = fs.readFileSync(imagePath);
    const name = imagePath.split(\"/\").pop();

    const up = new FormData();
    up.append(\"name\", name);
    up.append(\"chunk\", \"0\");
    up.append(\"chunks\", \"1\");
    up.append(\"task\", task);
    up.append(\"preview\", \"1\");
    up.append(\"file\", file, { filename: name, contentType: \"image/jpeg\" });

    const upload = await axios.post(\"https://api5g.iloveimg.com/v1/upload\", up, {
      headers: {
        ...up.getHeaders(),
        Authorization: `Bearer ${token}`,
        origin: \"https://www.iloveimg.com\",
        referer: \"https://www.iloveimg.com/\",
      },
    });

    const proses = await axios.post(
      \"https://api5g.iloveimg.com/v1/removebackground\",
      new URLSearchParams({
        task,
        server_filename: upload.data.server_filename,
      }).toString(),
      {
        responseType: \"arraybuffer\",
        headers: {
          Authorization: `Bearer ${token}`,
          \"Content-Type\": \"application/x-www-form-urlencoded\",
          origin: \"https://www.iloveimg.com\",
          referer: \"https://www.iloveimg.com/\",
        },
      }
    );

    const out = new FormData();
    out.append(\"files[]\", proses.data, { filename: \"output.png\" });

    const res = await axios.post(\"https://uguu.se/upload\", out, {
      headers: out.getHeaders(),
    });

    return res.data.files[0].url;
  } catch (e) {
    throw new Error(\"Gagal proses: \" + e.message);
  }
}

// ====================== PLUGIN ======================
exports.run = {
  usage: [\"removebackground\"],
  hidden: [\"rbg\"],
  use: \"kirim / reply gambar\",
  category: \"ai\",
  limit: true,
  location: \"plugins/ai/removebackground.js\",

  async: async (m, { kuromi }) => {
    let quoted = m.quoted || m;
    if (!quoted.mime || !/image/.test(quoted.mime))
      return m.reply(\"Kirim atau reply gambar untuk hapus background.\");

    kuromi.sendReact(m.chat, \"ðŸ•’\", m.key);

    try {
      // simpan gambar sementara
      const file = await kuromi.downloadAndSaveMediaMessage(quoted);
      const resultUrl = await removebg(file);

      // kirim hasil
      await kuromi.sendMessage(
        m.chat,
        {
          image: { url: resultUrl },
          caption: \"*Background berhasil dihapus!*\"
        },
        { quoted: quoted } // balas ke gambar yg direply
      );

      fs.unlinkSync(file);
      kuromi.sendReact(m.chat, \"âœ…\", m.key);

    } catch (err) {
      kuromi.sendReact(m.chat, \"âŒ\", m.key);
      m.reply(err.message || String(err));
    }
  }
};

