        // Ambil berita pertama untuk dikirim dengan gambar
        const firstNews = news[0];
        const caption = `📰 *${firstNews.title}*\n\n📅 *Tanggal:* ${firstNews.date}\n✍️ *Penulis:* ${firstNews.author}\n📂 *Kategori:* ${firstNews.category}\n\n${firstNews.excerpt}\n\n🔗 *Baca Selengkapnya:* ${firstNews.url}`;

        // Kirim berita pertama dengan gambar
        await kuromi.sendMessage(m.chat, { image: { url: firstNews.img }, caption }, { quoted: m });

        // Kirim 4 berita lainnya dalam bentuk teks
        let text = `📰 *Berita Lainnya:*\n\n`;
        news.slice(1, 5).forEach((article, i) => {
            text += `*${i + 2}. ${article.title}*\n📅 ${article.date} | ✍️ ${article.author}\n🔗 ${article.url}\n\n`;
        });

        kuromi.sendMessage(m.chat, { text }, { quoted: m });
    }
};

const axios = require(\"axios\");
const cheerio = require(\"cheerio\");

async function scrapeKaoriNews() {
  try {
    const response = await axios.get(\"https://www.kaorinusantara.or.id/newsline\");
    const html = response.data;
    const $ = cheerio.load(html);

    let articles = [];

    $(\".td_module_10\").each((i, el) => {
      const title = $(el).find(\".entry-title a\").text().trim();
      const link = $(el).find(\".entry-title a\").attr(\"href\");
      const description = $(el).find(\".td-excerpt\").text().trim();
      const image = $(el).find(\".td-module-thumb img\").attr(\"data-src\") || $(el).find(\".td-module-thumb img\").attr(\"src\");

      articles.push({ title, link, description, image });
    });

    return articles;
  } catch (e) {
    console.error(\"Error fetching data:\", e.message);
    return [];
  }
}

