let multidb = new (require('../../system/multidb.js'));
exports.run = {
  usage: ['restart'],
  category: 'owner',
  async: async (e, { func: a, mecha: t }) => {
    await t.sendMessage(e.chat, { text: a.texted('monospace', 'Restarting...') }, { quoted: e, ephemeralExpiration: e.expiration }).then(async () => {
      if (await multidb.init.save(global.db), !process.send) return e.reply('Tidak dapat me-restart bot karena file `index.js` tidak dijalankan.');
      process.send('reset');
    });
  },
  devs: true
};
