const axios = require('axios');

exports.run = {
  usage: ['tiktokstalk'],
  hidden: ['ttstalk'],
  use: 'username',
  category: 'search',
  async: async (m, { text, kuromi, isPrem }) => {
    if (!text) return m.reply('⚠️ Masukkan username TikTok!\n\nContoh: *.tiktokstalk sanz.co99*');

    try {
      const apiUrl = `https://api.sxtream.xyz/stalk/tiktokstalk?username=${encodeURIComponent(text)}`;
      const { data } = await axios.get(apiUrl);

      if (!data || !data.result || !data.result.user) 
        return m.reply('❌ Tidak dapat menemukan pengguna tersebut.');

      const user = data.result.user;
      const stats = data.result.stats;

      const caption = `
👤 *TIKTOK STALKER RESULT*

📛 *Nama:* ${user.nickname || '-'}
🔖 *Username:* @${user.uniqueId || '-'}
🆔 *User ID:* ${user.id || '-'}
🌐 *Verified:* ${user.verified ? '✅ Ya' : '❌ Tidak'}
📅 *Dibuat:* ${new Date(user.createTime * 1000).toLocaleDateString('id-ID')}
📝 *Bio:* ${user.signature || 'Tidak ada bio'}

📊 *Statistik:*
👥 *Followers:* ${stats.followerCount}
👤 *Following:* ${stats.followingCount}
❤️ *Likes:* ${stats.heartCount}
🎥 *Video:* ${stats.videoCount}
🤝 *Teman:* ${stats.friendCount}

🌍 *Bahasa:* ${user.language || '-'}
`;

      await kuromi.sendMessage(m.chat, {
        image: { url: user.avatarLarger },
        caption
      }, { quoted: m });

    } catch (err) {
      console.error('❌ TikTok Stalk Error:', err);
      m.reply(`❌ Gagal mengambil data TikTok.\n📄 *Error:* ${err.message}`);
    }
  },
  location: \"plugins/search/tiktokstalk.js\",
  premium: true
};

