const normalizeJid = (jid) => {
    if (!jid) return jid;
    if (typeof jid !== \"string\") jid = String(jid);
    jid = jid.trim();

    // biarin untuk special JID
    if (
        jid === \"status@broadcast\" ||
        jid.endsWith(\"@g.us\") ||
        jid.endsWith(\"@newsletter\") ||
        jid.endsWith(\"@s.whatsapp.net\")
    ) {
        try {
            return jidNormalizedUser(jid);
        } catch {
            return jid;
        }
    }

    // kalau cuma nomor / ada symbol +/spasi
    const numeric = jid.replace(/[^0-9]/g, \"\");
    if (!numeric) return jid;

    const userJid = numeric + \"@s.whatsapp.net\";
    try {
        return jidNormalizedUser(userJid);
    } catch {
        return userJid;
    }
};

