            return 0 === this ? \"0 Bytes\" : (e = e < 0 ? 0 : e, t = Math.floor(Math.log(this) / Math.log(1024)), parseFloat((this / Math.pow(1024, t)).toFixed(e)) + \" \" + [\"Bytes\", \"KB\", \"MB\", \"GB\", \"TB\", \"PB\", \"EB\", \"ZB\", \"YB\"][t]);
        };

        Number.prototype.toTimeString = function () {
            var e = Math.floor(this / 1e3 % 60),
                t = Math.floor(this / 6e4 % 60),
                s = Math.floor(this / 36e5 % 24),
                a = Math.floor(this / 864e5);
            return ((a ? a + \" day \" : \"\") + (s ? s + \" hour \" : \"\") + (t ? t + \" minute \" : \"\") + (e ? e + \" second\" : \"\")).trim();
        };
    };
};


