const axios = require(\"axios\");

function filterDuplicates(arr) {
    return [...new Set(arr)];
}

exports.run = {
    usage: [\"instagram\"],
    hidden: [\"igdl\", \"ig\"],
    use: \"link instagram\",
    category: \"downloader\",

    async: async (m, { func, kuromi }) => {

        if (!m.text)
            return m.reply(
                func.example(
                    m.cmd,
                    \"https://www.instagram.com/reel/DRqqxNTksgF/?igsh=MWI1ZjRhc2IzNDBxNQ==\"
                )
            );

        if (
            !m.args[0].match(
                /https:\/\/www\.instagram\.com\/(share\/(p|reel|tv)\/[A-Za-z0-9_-]+|reel\/[A-Za-z0-9_-]+\/?\??.*)/gi
            )
        )
            return m.reply(`Link salah.

contoh:
${m.cmd} https://www.instagram.com/reel/DRqqxNTksgF/?igsh=MWI1ZjRhc2IzNDBxNQ==`);

        await kuromi.sendReact(m.chat, \"🕒\", m.key);

        let ig;
        try {
            const { data } = await axios.get(
                \"https://api.nekolabs.web.id/downloader/instagram?url=\" +
                encodeURIComponent(m.args[0]),
                {
                    headers: {
                        \"user-agent\": \"Mozilla/5.0 (Windows NT 10.0; Win64; x64)\"
                    }
                }
            );
            ig = data;
        } catch (err) {
            return m.reply(\"Gagal mengambil data dari API Nekolabs.\");
        }

        if (!ig.success) return m.reply(\"Terjadi kesalahan API.\");
        if (!ig.result || !ig.result.downloadUrl) return m.reply(\"Data kosong.\");

        const meta = ig.result.metadata;
        const list = filterDuplicates(ig.result.downloadUrl);

        let caption = `*INSTAGRAM DOWNLOADER*

*Username:* ${meta?.username || \"-\"}
*Caption:* ${meta?.caption || \"-\"}
*Like:* ${meta?.like || 0}
*Komentar:* ${meta?.comment || 0}
*Video:* ${meta?.isVideo ? \"Ya\" : \"Tidak\"}
*Jumlah Media:* ${list.length}

URL:
${m.args[0]}`;

        // kirim caption + media pertama
        await kuromi.sendMedia(m.chat, list[0], m, {
            caption: caption,
            expiration: m.expiration
        });

        // kirim sisanya tanpa caption
        if (list.length > 1) {
            for (let i = 1; i < list.length; i++) {
                await kuromi.sendMedia(m.chat, list[i], m, {
                    caption: \"\",
                    expiration: m.expiration
                });
            }
        }

        await kuromi.sendReact(m.chat, \"✅\", m.key);
    },

    premium: false,
    limit: 5,
    location: \"plugins/downloader/instagram.js\"
};

