const axios = require(\"axios\");

exports.run = {
    usage: [\"mediafire\"],
    hidden: [\"mfire\", \"mfdl\"],
    use: \"link mediafire\",
    category: \"downloader\",

    async: async (m, { func, kuromi }) => {

        if (!m.text)
            return m.reply(
                func.example(
                    m.cmd,
                    \"https://www.mediafire.com/file/a61862y1tgvfiim/ZackBotMans+(+Versi+1.0.1+).zip/file\"
                )
            );

        if (!m.args[0].includes(\"mediafire.com\"))
            return m.reply(global.mess.error.url);

        await kuromi.sendReact(m.chat, \"🕒\", m.key);

        let json;
        try {
            const { data } = await axios.get(
                \"https://api.nekolabs.web.id/downloader/mediafire?url=\" +
                encodeURIComponent(m.args[0]),
                {
                    headers: {
                        \"user-agent\": \"Mozilla/5.0 (Windows NT 10.0; Win64; x64)\"
                    }
                }
            );
            json = data;
        } catch (err) {
            return m.reply(\"Gagal mengambil data dari API Nekolabs.\");
        }

        if (!json.success)
            return m.reply(\"Something went wrong!\");

        const res = json.result;

        if (!res || !res.download_url)
            return m.reply(\"Tidak dapat menemukan link download!\");

        const caption = `乂  *AUTO MEDIAFIRE DOWNLOADER*

◦ *File Name:* ${res.filename}
◦ *File Size:* ${res.filesize}
◦ *MimeType:* ${res.mimetype}
◦ *Uploaded:* ${res.uploaded}

_Please wait media is being sent..._`;

        await kuromi.sendMessage(
            m.chat,
            { text: caption },
            { quoted: m, ephemeralExpiration: m.expiration }
        );

        await kuromi.sendMedia(m.chat, res.download_url, m, {
            caption: global.mess.ok,
            fileName: res.filename,
            mimetype: res.mimetype,
            expiration: m.expiration
        });

        await kuromi.sendReact(m.chat, \"✅\", m.key);
    },

    limit: 3,
    location: \"plugins/downloader/mediafire.js\"
};
