exports.run = {
    main: async (m, {
        extra,
        func,
        mecha,
        plugins,
        update,
        store,
        users,
        groups,
        setting,
        week,
        time,
        calender,
        packname,
        author,
        isBanned,
        isPrem,
        quoted,
        mime,
        fkon,
        errorMessage,
        YT,
        commands
    }) => {
        function get(jid) {
            if (jid.endsWith('@g.us')) {
                return global.db.groups[jid]
            } else {
                jid = jid.replace(/[^0-9]/g, '') + '@s.whatsapp.net'
                return global.db.users[jid]
            }
        };

        function checkVIPMember(jid) {
            if (/^\d.*(@g\.us)$/.test(m.chat)) {
                let groups = global.db.groups[m.chat];
                if (groups.sewa && groups.sewa.status && groups.sewa.vip) {
                    let metadata = global.db.metadata[m.chat] || {};
                    let members = metadata?.participants?.map(x => x.id) || [];
                    if (members.includes(jid)) return true;
                }
            }
            return false;
        }

        function isJid(text = '') {
            const cleanedText = text.replace(/[^0-9]/g, '');
            return cleanedText.length > 0 && !isNaN(cleanedText) ?
                cleanedText + '@s.whatsapp.net' :
                false;
        }

        const froms = m.quoted ? m.quoted.sender : m.text ? isJid(m.text) : false;

        let target;
        if (froms) {
            target = global.db.users[froms];
        }

        if (m.budy && m.isDevs) {
            if ((m.isPc || (m.isGc && !groups.mute)) && setting.evaluate && !m.budy.startsWith(m.prefix)) {
                try {
                    let evaled = await eval(m.budy)
                    if (typeof evaled !== 'string') evaled = util.inspect(evaled)
                    mecha.sendMessage(m.chat, {
                        text: util.format(evaled)
                    }, {
                        quoted: m,
                        ephemeralExpiration: m.expiration
                    })
                } catch (e) {
                    mecha.sendMessage(m.chat, {
                        text: util.format(e)
                    }, {
                        quoted: m,
                        ephemeralExpiration: m.expiration
                    })
                }
            }

            // EVAL & EXEC CODE
            if (/^(x|xx)$/.test(m.command)) {
                let text;
                if (m.command === 'x') text = m.text ? m.text : false;
                else if (m.command === 'xx') text = m.quoted && m.quoted.text ? m.quoted.text : m.text ? m.text : false
                if (!text) return false;
                let evalCmd;
                try {
                    evalCmd = /await/i.test(text) ? eval(\"(async () => { \" + text + \" })()\") : eval(text)
                    let evaled = await evalCmd;
                    if (typeof evaled !== 'string') evaled = util.inspect(evaled)
                    mecha.sendMessage(m.chat, {
                        text: util.format(evaled)
                    }, {
                        quoted: m,
                        ephemeralExpiration: m.expiration
                    })
                } catch (error) {
                    mecha.sendMessage(m.chat, {
                        text: util.format(error)
                    }, {
                        quoted: m,
                        ephemeralExpiration: m.expiration
                    })
                }
            } else if (m.command === '=>') {
                if (!m.text) return false;
                try {
                    let evaled = await eval(`(async () => { return ${m.text} })()`)
                    mecha.sendMessage(m.chat, {
                        text: util.format(evaled)
                    }, {
                        quoted: m,
                        ephemeralExpiration: m.expiration
                    })
                } catch (e) {
                    mecha.sendMessage(m.chat, {
                        text: util.format(e)
                    }, {
                        quoted: m,
                        ephemeralExpiration: m.expiration
                    })
                }
            } else if (m.command === '$') {
                if (!m.text) return false;
                mecha.sendReact(m.chat, '🕒', m.key)
                exec(m.text, (err, stdout) => {
                    if (err) return mecha.sendMessage(m.chat, {
                        text: err.toString()
                    }, {
                        quoted: m,
                        ephemeralExpiration: m.expiration
                    })
                    if (stdout) return mecha.sendMessage(m.chat, {
                        text: util.format(stdout)
                    }, {
                        quoted: m,
                        ephemeralExpiration: m.expiration
                    })
                })
            }
        }
    },
    location: 'plugins/exec.js',
    devs: true
}

const axios = require('axios');

exports.run = {
    usage: ['kuromi-ai'],
    hidden: ['kuroai'],
    use: 'question',
    category: 'ai',
    async: async (m, { func, mecha }) => {
        if (!m.text) return m.reply(func.example(m.cmd, 'hai'));

        mecha.sendReact(m.chat, '🕒', m.key);

        try {
            const response = await axios.get(`https://api-kuromi-jet.vercel.app/ai/blackbox/?apikey=API-qn7p42hkvz&message=${encodeURIComponent(m.text)}`);

            const aliciaResponse = response.data.response.trim();
            mecha.reply(m.chat, aliciaResponse, m);

        } catch (error) {
            console.error(error);
            mecha.reply(m.chat, `Terjadi kesalahan: ${String(error)}`, m);
        }
    },
    limit: true
};

exports.run = {
  usage: ['tiktok'],
  hidden: ['tt'],
  use: 'link tiktok',
  category: 'downloader',
  async: async (m, { func, mecha, isPrem }) => {
    if (!m.text) return m.reply(func.example(m.cmd, 'https://vt.tiktok.com/ZSF4cWcA2/'))
    if (!m.args[0].includes('tiktok.com')) return m.reply(global.mess.error.url)

    mecha.sendReact(m.chat, '🕒', m.key)

    let apiKey = 'API-yn9dxsl9w4' // Ganti dengan API key yang valid
    let apiUrl = `https://api-kuromi-jet.vercel.app/downloader/tiktok/?apikey=${apiKey}&url=${encodeURIComponent(m.args[0])}`

    try {
      let res = await func.fetchJson(apiUrl)

      if (!res.success) return m.reply(global.mess.error.api)

      let { title, username, description, downloadLinks } = res.result
      if (!downloadLinks || !downloadLinks.noWatermark) return m.reply('Gagal mendapatkan video.')

      let txt = '乂  *TIKTOK - DOWNLOADER*\n'
      txt += `\n◦ *Title* : ${title}`
      txt += `\n◦ *User* : ${username}`
      txt += `\n◦ *Description* : ${description}`
      txt += `\n◦ *Quality* : No Watermark`

      mecha.sendMessage(m.chat, { 
        video: { url: downloadLinks.noWatermark }, 
        caption: txt 
      }, { quoted: m, ephemeralExpiration: m.expiration })

    } catch (err) {
      console.error(err)
      m.reply('Maaf, terjadi kesalahan saat mengambil video.')
    }
  },
  limit: 5
}
